client/src/pages/admin/user-detail.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { useRoute, useLocation } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { ArrowLeft, Check, Ban, DollarSign } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import type { User } from "@shared/schema";
import { useState } from "react";

export default function AdminUserDetail() {
  const [, params] = useRoute("/admin/users/:id");
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [depositAmount, setDepositAmount] = useState("");

  const userId = parseInt(params?.id || "0");

  const { data: user, isLoading } = useQuery<User>({
    queryKey: [`/api/admin/users/${userId}`],
    enabled: userId > 0,
  });

  const updateStatusMutation = useMutation({
    mutationFn: async (status: string) => {
      return apiRequest("POST", `/api/admin/users/${userId}/status`, { status });
    },
    onSuccess: () => {
      toast({
        title: "Status Updated",
        description: "User status has been updated successfully.",
      });
      queryClient.invalidateQueries({ queryKey: [`/api/admin/users/${userId}`] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Update Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const depositMutation = useMutation({
    mutationFn: async (amount: string) => {
      const response = await fetch(`/api/admin/users/${userId}/deposit`, {
        method: "POST",
        body: JSON.stringify({ amount }),
        headers: { "Content-Type": "application/json" },
      });
      if (!response.ok) {
        const error = await response.text();
        throw new Error(error);
      }
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Deposit Added",
        description: "The deposit has been added to the user's account successfully.",
      });
      queryClient.invalidateQueries({ queryKey: [`/api/admin/users/${userId}`] });
      setDepositAmount("");
    },
    onError: (error: Error) => {
      toast({
        title: "Deposit Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleDeposit = () => {
    const amount = parseFloat(depositAmount);
    if (isNaN(amount) || amount <= 0) {
      toast({
        variant: "destructive",
        title: "Invalid Amount",
        description: "Please enter a valid deposit amount",
      });
      return;
    }
    depositMutation.mutate(depositAmount);
  };

  const getStatusVariant = (status: string): "default" | "secondary" | "destructive" => {
    switch (status) {
      case "active":
        return "default";
      case "pending":
        return "secondary";
      case "frozen":
        return "destructive";
      default:
        return "secondary";
    }
  };

  if (isLoading) {
    return <div className="text-center py-8">Loading user details...</div>;
  }

  if (!user) {
    return (
      <div className="text-center py-8">
        <p className="text-muted-foreground mb-4">User not found</p>
        <Button onClick={() => setLocation("/admin/users")}>Go Back</Button>
      </div>
    );
  }

  return (
    <div>
      <Button
        variant="ghost"
        onClick={() => setLocation("/admin/users")}
        className="mb-6"
        data-testid="button-back"
      >
        <ArrowLeft className="h-4 w-4 mr-2" />
        Back to Users
      </Button>

      <div className="grid gap-6 md:grid-cols-2">
        <Card>
          <CardHeader>
            <CardTitle>User Information</CardTitle>
            <CardDescription>Complete user details</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <p className="text-sm font-medium text-muted-foreground">Full Name</p>
              <p className="text-lg" data-testid="text-fullname">
                {user.fullName}
              </p>
            </div>
            <div>
              <p className="text-sm font-medium text-muted-foreground">Username</p>
              <p className="text-lg" data-testid="text-username">
                @{user.username}
              </p>
            </div>
            <div>
              <p className="text-sm font-medium text-muted-foreground">Email</p>
              <p className="text-lg" data-testid="text-email">
                {user.email}
              </p>
            </div>
            <div>
              <p className="text-sm font-medium text-muted-foreground">Registered Date</p>
              <p className="text-lg">
                {new Date(user.registeredAt).toLocaleDateString()}{" "}
                {new Date(user.registeredAt).toLocaleTimeString()}
              </p>
            </div>
            <div>
              <p className="text-sm font-medium text-muted-foreground">Account Type</p>
              <p className="text-lg">
                {user.isAdmin === 1 ? "Administrator" : "Regular User"}
              </p>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Account Status</CardTitle>
            <CardDescription>Manage user account status</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <p className="text-sm font-medium text-muted-foreground mb-2">Current Status</p>
              <Badge
                variant={getStatusVariant(user.status)}
                className="text-base px-4 py-2"
                data-testid="badge-status"
              >
                {user.status.charAt(0).toUpperCase() + user.status.slice(1)}
              </Badge>
            </div>

            <div className="space-y-2 pt-4">
              <p className="text-sm font-medium text-muted-foreground">Quick Actions</p>
              <div className="flex flex-col gap-2">
                {user.status !== "active" && (
                  <Button
                    className="w-full"
                    onClick={() => updateStatusMutation.mutate("active")}
                    disabled={updateStatusMutation.isPending}
                    data-testid="button-approve"
                  >
                    <Check className="h-4 w-4 mr-2" />
                    Approve Account
                  </Button>
                )}
                {user.status !== "frozen" && (
                  <Button
                    variant="destructive"
                    className="w-full"
                    onClick={() => updateStatusMutation.mutate("frozen")}
                    disabled={updateStatusMutation.isPending}
                    data-testid="button-freeze"
                  >
                    <Ban className="h-4 w-4 mr-2" />
                    Freeze Account
                  </Button>
                )}
                {user.status === "frozen" && (
                  <Button
                    variant="outline"
                    className="w-full"
                    onClick={() => updateStatusMutation.mutate("active")}
                    disabled={updateStatusMutation.isPending}
                    data-testid="button-unfreeze"
                  >
                    <Check className="h-4 w-4 mr-2" />
                    Unfreeze Account
                  </Button>
                )}
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Financial Information */}
      <div className="grid gap-6 md:grid-cols-2 mt-6">
        <Card>
          <CardHeader>
            <CardTitle>Financial Information</CardTitle>
            <CardDescription>User's balance and transaction details</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <p className="text-sm font-medium text-muted-foreground">Destination Amount</p>
              <p className="text-2xl font-bold text-amber-500" data-testid="text-destination-amount">
                ₹ {parseFloat(user.destinationAmount).toLocaleString('en-IN', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
              </p>
              <p className="text-xs text-muted-foreground">Registration bonus</p>
            </div>
            <div>
              <p className="text-sm font-medium text-muted-foreground">Milestone Amount</p>
              <p className="text-2xl font-bold text-green-500" data-testid="text-milestone-amount">
                ₹ {parseFloat(user.milestoneAmount).toLocaleString('en-IN', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
              </p>
              <p className="text-xs text-muted-foreground">Withdrawable balance</p>
            </div>
            <div>
              <p className="text-sm font-medium text-muted-foreground">Milestone Reward</p>
              <p className="text-2xl font-bold text-blue-500" data-testid="text-milestone-reward">
                ₹ {parseFloat(user.milestoneReward).toLocaleString('en-IN', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
              </p>
              <p className="text-xs text-muted-foreground">Total withdrawn</p>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Add Deposit</CardTitle>
            <CardDescription>Add funds to user's Milestone Amount</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="deposit-amount">Deposit Amount (LKR)</Label>
              <Input
                id="deposit-amount"
                type="number"
                placeholder="Enter amount"
                value={depositAmount}
                onChange={(e) => setDepositAmount(e.target.value)}
                data-testid="input-deposit-amount"
              />
            </div>
            <Button
              onClick={handleDeposit}
              disabled={depositMutation.isPending || !depositAmount}
              className="w-full"
              data-testid="button-add-deposit"
            >
              <DollarSign className="h-4 w-4 mr-2" />
              {depositMutation.isPending ? "Adding..." : "Add Deposit"}
            </Button>
            <p className="text-sm text-muted-foreground">
              This will be added to the user's withdrawable balance (Milestone Amount).
            </p>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/admin/premium.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { useState } from "react";
import type { User } from "@shared/schema";
import { Search, Mail, Phone } from "lucide-react";
import { RestrictUserDialog } from "@/components/restrict-user-dialog";
import { ResetConfirmDialog } from "@/components/reset-confirm-dialog";
import { AddValueDialog } from "@/components/add-value-dialog";
import { EditUserDialog, type EditUserData } from "@/components/edit-user-dialog";
import { EditBankDialog, type EditBankData } from "@/components/edit-bank-dialog";

export default function AdminPremium() {
  const { toast } = useToast();
  const [searchQuery, setSearchQuery] = useState("");
  const [restrictDialogOpen, setRestrictDialogOpen] = useState(false);
  const [dialogMode, setDialogMode] = useState<"create" | "edit">("create");
  const [resetDialogOpen, setResetDialogOpen] = useState(false);
  const [addValueDialogOpen, setAddValueDialogOpen] = useState(false);
  const [editUserDialogOpen, setEditUserDialogOpen] = useState(false);
  const [editBankDialogOpen, setEditBankDialogOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [resetField, setResetField] = useState<string>("");
  const [addValueField, setAddValueField] = useState<string>("");

  const { data: users = [], isLoading } = useQuery<User[]>({
    queryKey: ["/api/admin/users"],
    refetchOnMount: "always",
    refetchOnWindowFocus: true,
  });

  // Filter users based on search query
  const filteredUsers = users.filter(user => {
    const query = searchQuery.toLowerCase();
    return (
      user.fullName.toLowerCase().includes(query) ||
      user.username.toLowerCase().includes(query) ||
      user.email.toLowerCase().includes(query) ||
      user.id.toString().includes(query)
    );
  });

  const setRestrictionMutation = useMutation({
    mutationFn: async ({ userId, adsLimit, deposit, commission, pendingAmount }: { 
      userId: number; 
      adsLimit: number; 
      deposit: string; 
      commission: string;
      pendingAmount: string;
    }) => {
      const response = await fetch(`/api/admin/users/${userId}/restrict`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ adsLimit, deposit, commission, pendingAmount }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || "Failed to set restriction");
      }

      return data;
    },
    onSuccess: () => {
      // Force refetch instead of just invalidating
      queryClient.refetchQueries({ queryKey: ["/api/admin/users"] });
      setRestrictDialogOpen(false);
      setSelectedUser(null);
      toast({
        title: "Restriction Applied",
        description: "User restriction has been set successfully. Milestone Amount has been reduced by the deposit amount.",
      });
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Restriction Failed",
        description: error.message || "Failed to apply restriction. Please check the input values.",
      });
    },
  });

  const resetMutation = useMutation({
    mutationFn: async ({ userId, field }: { userId: number; field: string }) => {
      const response = await fetch(`/api/admin/users/${userId}/reset`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ field }),
      });
      if (!response.ok) throw new Error("Failed to reset");
      return response.json();
    },
    onSuccess: () => {
      // Force refetch instead of just invalidating
      queryClient.refetchQueries({ queryKey: ["/api/admin/users"] });
      toast({
        title: "Reset Successful",
        description: "Field has been reset to 0",
      });
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Reset Failed",
        description: error.message,
      });
    },
  });

  const addValueMutation = useMutation({
    mutationFn: async ({ userId, field, amount }: { userId: number; field: string; amount: string }) => {
      const response = await fetch(`/api/admin/users/${userId}/add-value`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ field, amount }),
      });
      if (!response.ok) throw new Error("Failed to add value");
      return response.json();
    },
    onSuccess: () => {
      // Force refetch instead of just invalidating
      queryClient.refetchQueries({ queryKey: ["/api/admin/users"] });
      toast({
        title: "Value Added",
        description: "Amount has been added successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Add Failed",
        description: error.message,
      });
    },
  });

  const editUserMutation = useMutation({
    mutationFn: async ({ userId, data }: { userId: number; data: EditUserData }) => {
      const response = await fetch(`/api/admin/users/${userId}/details`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const error = await response.text();
        throw new Error(error || "Failed to update user details");
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.refetchQueries({ queryKey: ["/api/admin/users"] });
      setEditUserDialogOpen(false);
      setSelectedUser(null);
      toast({
        title: "User Updated",
        description: "User details have been updated successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Update Failed",
        description: error.message || "Failed to update user details.",
      });
    },
  });

  const editBankMutation = useMutation({
    mutationFn: async ({ userId, data }: { userId: number; data: EditBankData }) => {
      const response = await fetch(`/api/admin/users/${userId}/bank`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const error = await response.text();
        throw new Error(error || "Failed to update bank details");
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.refetchQueries({ queryKey: ["/api/admin/users"] });
      setEditBankDialogOpen(false);
      setSelectedUser(null);
      toast({
        title: "Bank Details Updated",
        description: "Bank details have been updated successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Update Failed",
        description: error.message || "Failed to update bank details.",
      });
    },
  });

  const handleRestrict = (userId: number) => {
    const user = users.find(u => u.id === userId);
    if (!user) return;

    setSelectedUser(user);

    // Check if user already has a restriction - if yes, open in EDIT mode
    if ((user as any).restrictionAdsLimit !== null && (user as any).restrictionAdsLimit !== undefined) {
      setDialogMode("edit");
    } else {
      setDialogMode("create");
    }

    setRestrictDialogOpen(true);
  };

  const handleRestrictionSubmit = (data: { adsLimit: number; deposit: string; commission: string; pendingAmount: string }) => {
    if (!selectedUser) return;
    setRestrictionMutation.mutate({
      userId: selectedUser.id,
      ...data,
    });
  };

  const handleReset = (userId: number, field: string) => {
    const user = users.find(u => u.id === userId);
    if (!user) return;

    setSelectedUser(user);
    setResetField(field);
    setResetDialogOpen(true);
  };

  const handleResetConfirm = () => {
    if (!selectedUser || !resetField) return;
    resetMutation.mutate({ userId: selectedUser.id, field: resetField });
    setResetDialogOpen(false);
    setSelectedUser(null);
    setResetField("");
  };

  const handleAdd = (userId: number, field: string) => {
    const user = users.find(u => u.id === userId);
    if (!user) return;

    setSelectedUser(user);
    setAddValueField(field);
    setAddValueDialogOpen(true);
  };

  const handleAddValueSubmit = (amount: string) => {
    if (!selectedUser || !addValueField) return;

    addValueMutation.mutate({ 
      userId: selectedUser.id, 
      field: addValueField, 
      amount 
    });

    setAddValueDialogOpen(false);
    setSelectedUser(null);
    setAddValueField("");
  };

  const handleEditUser = (userId: number) => {
    const user = users.find(u => u.id === userId);
    if (!user) return;

    setSelectedUser(user);
    setEditUserDialogOpen(true);
  };

  const handleEditUserSubmit = (data: EditUserData) => {
    if (!selectedUser) return;
    editUserMutation.mutate({ userId: selectedUser.id, data });
  };

  const handleEditBank = (userId: number) => {
    const user = users.find(u => u.id === userId);
    if (!user) return;

    setSelectedUser(user);
    setEditBankDialogOpen(true);
  };

  const handleEditBankSubmit = (data: EditBankData) => {
    if (!selectedUser) return;
    editBankMutation.mutate({ userId: selectedUser.id, data });
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-96">
        <p className="text-muted-foreground">Loading premium users...</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between bg-card border rounded-lg p-4">
        <h1 className="text-xl font-bold" style={{ color: '#B8936B' }}>
          Premium
        </h1>
        <div className="flex items-center gap-4 flex-1 max-w-2xl mx-8">
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <Input
              placeholder="Search"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10"
              data-testid="input-search-premium"
            />
          </div>
        </div>
        <p className="text-sm text-muted-foreground">
          Hello, did you check the List of Premium Packages? 📦
        </p>
      </div>

      {/* Premium Users Table */}
      <Card>
        <CardContent className="p-0">
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-muted/50">
                <tr>
                  <th className="px-3 py-3 text-left text-xs font-semibold uppercase tracking-wider" style={{ color: '#B8936B' }}>ID</th>
                  <th className="px-3 py-3 text-left text-xs font-semibold uppercase tracking-wider" style={{ color: '#B8936B' }}>Name</th>
                  <th className="px-3 py-3 text-left text-xs font-semibold uppercase tracking-wider" style={{ color: '#B8936B' }}>Email</th>
                  <th className="px-3 py-3 text-left text-xs font-semibold uppercase tracking-wider" style={{ color: '#B8936B' }}>Invitation Code</th>
                  <th className="px-3 py-3 text-left text-xs font-semibold uppercase tracking-wider" style={{ color: '#B8936B' }}>Phone</th>
                  <th className="px-3 py-3 text-left text-xs font-semibold uppercase tracking-wider" style={{ color: '#B8936B' }}>Total Booking</th>
                  <th className="px-3 py-3 text-left text-xs font-semibold uppercase tracking-wider" style={{ color: '#B8936B' }}>Booking</th>
                  <th className="px-3 py-3 text-left text-xs font-semibold uppercase tracking-wider" style={{ color: '#B8936B' }}>Points</th>
                  <th className="px-3 py-3 text-left text-xs font-semibold uppercase tracking-wider" style={{ color: '#B8936B' }}>Premium Treasure</th>
                  <th className="px-3 py-3 text-left text-xs font-semibold uppercase tracking-wider" style={{ color: '#B8936B' }}>Normal Treasure</th>
                  <th className="px-3 py-3 text-left text-xs font-semibold uppercase tracking-wider" style={{ color: '#B8936B' }}>Booking Value</th>
                  <th className="px-3 py-3 text-left text-xs font-semibold uppercase tracking-wider" style={{ color: '#B8936B' }}>Edit User</th>
                  <th className="px-3 py-3 text-left text-xs font-semibold uppercase tracking-wider" style={{ color: '#B8936B' }}>Bank Details</th>
                </tr>
              </thead>
              <tbody className="divide-y">
                {filteredUsers.length === 0 ? (
                  <tr>
                    <td colSpan={13} className="px-4 py-8 text-center text-muted-foreground">
                      {searchQuery ? "No users found matching your search" : "No users registered yet"}
                    </td>
                  </tr>
                ) : (
                  filteredUsers.map((user) => (
                    <tr key={user.id} className="hover:bg-muted/50" data-testid={`row-user-${user.id}`}>
                      <td className="px-3 py-3 text-sm">{user.id}</td>
                      <td className="px-3 py-3 text-sm font-medium">{user.fullName}</td>
                      <td className="px-3 py-3 text-sm">
                        <div className="flex items-center gap-1">
                          <Mail className="h-3 w-3 text-muted-foreground" />
                          <span className="text-xs">{user.email}</span>
                        </div>
                      </td>
                      <td className="px-3 py-3 text-sm text-center font-semibold">
                        {user.id * 15 + 60}
                      </td>
                      <td className="px-3 py-3 text-sm">
                        <div className="flex items-center gap-1">
                          <Phone className="h-3 w-3 text-muted-foreground" />
                          <span className="text-xs">{user.id}1892</span>
                        </div>
                      </td>
                      <td className="px-3 py-3">
                        <div className="flex flex-col gap-1">
                          <span className="text-xs font-medium" data-testid={`text-total-booking-${user.id}`}>
                            {(user as any).totalAdsCompleted || 0}/{(user as any).restrictionAdsLimit || ''}
                          </span>
                          <Button
                            size="sm"
                            className="bg-amber-600 hover:bg-amber-700 text-white text-xs h-6 px-2"
                            onClick={() => handleRestrict(user.id)}
                            data-testid={`button-promotions-${user.id}`}
                          >
                            Promotions
                          </Button>
                        </div>
                      </td>
                      <td className="px-3 py-3">
                        <Button
                          size="sm"
                          className="bg-amber-600 hover:bg-amber-700 text-white text-xs h-6 px-3"
                          onClick={() => handleReset(user.id, 'booking')}
                          data-testid={`button-reset-booking-${user.id}`}
                        >
                          RESET
                        </Button>
                      </td>
                      <td className="px-3 py-3">
                        <div className="flex flex-col gap-1">
                          <span className="text-xs font-medium" data-testid={`text-points-${user.id}`}>
                            {user.points || 0}
                          </span>
                          <Button
                            size="sm"
                            className="bg-amber-600 hover:bg-amber-700 text-white text-xs h-6 px-3"
                            onClick={() => handleAdd(user.id, 'points')}
                            data-testid={`button-add-points-${user.id}`}
                          >
                            SET
                          </Button>
                        </div>
                      </td>
                      <td className="px-3 py-3">
                        <Button
                          size="sm"
                          className="bg-amber-600 hover:bg-amber-700 text-white text-xs h-6 px-3"
                          onClick={() => handleAdd(user.id, 'premiumTreasure')}
                          data-testid={`button-add-premium-${user.id}`}
                        >
                          ADD
                        </Button>
                      </td>
                      <td className="px-3 py-3">
                        <Button
                          size="sm"
                          className="bg-amber-600 hover:bg-amber-700 text-white text-xs h-6 px-3"
                          onClick={() => handleAdd(user.id, 'normalTreasure')}
                          data-testid={`button-add-normal-${user.id}`}
                        >
                          ADD
                        </Button>
                      </td>
                      <td className="px-3 py-3">
                        <Button
                          size="sm"
                          className="bg-amber-600 hover:bg-amber-700 text-white text-xs h-6 px-3"
                          onClick={() => handleAdd(user.id, 'bookingValue')}
                          data-testid={`button-add-value-${user.id}`}
                        >
                          ADD
                        </Button>
                      </td>
                      <td className="px-3 py-3">
                        <Button
                          size="sm"
                          className="bg-blue-600 hover:bg-blue-700 text-white text-xs h-6 px-3"
                          onClick={() => handleEditUser(user.id)}
                          data-testid={`button-edit-user-${user.id}`}
                        >
                          EDIT
                        </Button>
                      </td>
                      <td className="px-3 py-3">
                        <Button
                          size="sm"
                          className="bg-green-600 hover:bg-green-700 text-white text-xs h-6 px-3"
                          onClick={() => handleEditBank(user.id)}
                          data-testid={`button-edit-bank-${user.id}`}
                        >
                          EDIT
                        </Button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </CardContent>
      </Card>

      {/* Promotion Dialog (Create/Edit) */}
      <RestrictUserDialog
        open={restrictDialogOpen}
        onOpenChange={setRestrictDialogOpen}
        userName={selectedUser?.fullName || ""}
        onSubmit={handleRestrictionSubmit}
        isPending={setRestrictionMutation.isPending}
        mode={dialogMode}
        defaultValues={
          dialogMode === "edit" && selectedUser
            ? {
                adsLimit: (selectedUser as any).restrictionAdsLimit,
                deposit: (selectedUser as any).restrictionDeposit,
                commission: (selectedUser as any).restrictionCommission,
                pendingAmount: (selectedUser as any).ongoingMilestone,
              }
            : undefined
        }
      />

      {/* Reset Confirm Dialog */}
      <ResetConfirmDialog
        open={resetDialogOpen}
        onOpenChange={setResetDialogOpen}
        userName={selectedUser?.fullName || ""}
        fieldName={resetField === 'booking' ? 'Ad Count' : resetField}
        onConfirm={handleResetConfirm}
      />

      {/* Add Value Dialog */}
      <AddValueDialog
        open={addValueDialogOpen}
        onOpenChange={setAddValueDialogOpen}
        userName={selectedUser?.fullName || ""}
        fieldName={addValueField}
        onSubmit={handleAddValueSubmit}
        isPending={addValueMutation.isPending}
      />

      {/* Edit User Dialog */}
      {selectedUser && (
        <EditUserDialog
          open={editUserDialogOpen}
          onOpenChange={setEditUserDialogOpen}
          user={{
            id: selectedUser.id,
            username: selectedUser.username,
            mobileNumber: selectedUser.mobileNumber,
          }}
          onSubmit={handleEditUserSubmit}
          isPending={editUserMutation.isPending}
        />
      )}

      {/* Edit Bank Dialog */}
      {selectedUser && (
        <EditBankDialog
          open={editBankDialogOpen}
          onOpenChange={setEditBankDialogOpen}
          user={{
            id: selectedUser.id,
            username: selectedUser.username,
            bankName: selectedUser.bankName,
            accountNumber: selectedUser.accountNumber,
            accountHolderName: selectedUser.accountHolderName,
            branchName: selectedUser.branchName,
          }}
          onSubmit={handleEditBankSubmit}
          isPending={editBankMutation.isPending}
        />
      )}
    </div>
  );
}

client/src/components/ui/carousel.tsx.content:
import * as React from "react"
import useEmblaCarousel, {
  type UseEmblaCarouselType,
} from "embla-carousel-react"
import { ArrowLeft, ArrowRight } from "lucide-react"

import { cn } from "@/lib/utils"
import { Button } from "@/components/ui/button"

type CarouselApi = UseEmblaCarouselType[1]
type UseCarouselParameters = Parameters<typeof useEmblaCarousel>
type CarouselOptions = UseCarouselParameters[0]
type CarouselPlugin = UseCarouselParameters[1]

type CarouselProps = {
  opts?: CarouselOptions
  plugins?: CarouselPlugin
  orientation?: "horizontal" | "vertical"
  setApi?: (api: CarouselApi) => void
}

type CarouselContextProps = {
  carouselRef: ReturnType<typeof useEmblaCarousel>[0]
  api: ReturnType<typeof useEmblaCarousel>[1]
  scrollPrev: () => void
  scrollNext: () => void
  canScrollPrev: boolean
  canScrollNext: boolean
} & CarouselProps

const CarouselContext = React.createContext<CarouselContextProps | null>(null)

function useCarousel() {
  const context = React.useContext(CarouselContext)

  if (!context) {
    throw new Error("useCarousel must be used within a <Carousel />")
  }

  return context
}

const Carousel = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement> & CarouselProps
>(
  (
    {
      orientation = "horizontal",
      opts,
      setApi,
      plugins,
      className,
      children,
      ...props
    },
    ref
  ) => {
    const [carouselRef, api] = useEmblaCarousel(
      {
        ...opts,
        axis: orientation === "horizontal" ? "x" : "y",
      },
      plugins
    )
    const [canScrollPrev, setCanScrollPrev] = React.useState(false)
    const [canScrollNext, setCanScrollNext] = React.useState(false)

    const onSelect = React.useCallback((api: CarouselApi) => {
      if (!api) {
        return
      }

      setCanScrollPrev(api.canScrollPrev())
      setCanScrollNext(api.canScrollNext())
    }, [])

    const scrollPrev = React.useCallback(() => {
      api?.scrollPrev()
    }, [api])

    const scrollNext = React.useCallback(() => {
      api?.scrollNext()
    }, [api])

    const handleKeyDown = React.useCallback(
      (event: React.KeyboardEvent<HTMLDivElement>) => {
        if (event.key === "ArrowLeft") {
          event.preventDefault()
          scrollPrev()
        } else if (event.key === "ArrowRight") {
          event.preventDefault()
          scrollNext()
        }
      },
      [scrollPrev, scrollNext]
    )

    React.useEffect(() => {
      if (!api || !setApi) {
        return
      }

      setApi(api)
    }, [api, setApi])

    React.useEffect(() => {
      if (!api) {
        return
      }

      onSelect(api)
      api.on("reInit", onSelect)
      api.on("select", onSelect)

      return () => {
        api?.off("select", onSelect)
      }
    }, [api, onSelect])

    return (
      <CarouselContext.Provider
        value={{
          carouselRef,
          api: api,
          opts,
          orientation:
            orientation || (opts?.axis === "y" ? "vertical" : "horizontal"),
          scrollPrev,
          scrollNext,
          canScrollPrev,
          canScrollNext,
        }}
      >
        <div
          ref={ref}
          onKeyDownCapture={handleKeyDown}
          className={cn("relative", className)}
          role="region"
          aria-roledescription="carousel"
          {...props}
        >
          {children}
        </div>
      </CarouselContext.Provider>
    )
  }
)
Carousel.displayName = "Carousel"

const CarouselContent = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const { carouselRef, orientation } = useCarousel()

  return (
    <div ref={carouselRef} className="overflow-hidden">
      <div
        ref={ref}
        className={cn(
          "flex",
          orientation === "horizontal" ? "-ml-4" : "-mt-4 flex-col",
          className
        )}
        {...props}
      />
    </div>
  )
})
CarouselContent.displayName = "CarouselContent"

const CarouselItem = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const { orientation } = useCarousel()

  return (
    <div
      ref={ref}
      role="group"
      aria-roledescription="slide"
      className={cn(
        "min-w-0 shrink-0 grow-0 basis-full",
        orientation === "horizontal" ? "pl-4" : "pt-4",
        className
      )}
      {...props}
    />
  )
})
CarouselItem.displayName = "CarouselItem"

const CarouselPrevious = React.forwardRef<
  HTMLButtonElement,
  React.ComponentProps<typeof Button>
>(({ className, variant = "outline", size = "icon", ...props }, ref) => {
  const { orientation, scrollPrev, canScrollPrev } = useCarousel()

  return (
    <Button
      ref={ref}
      variant={variant}
      size={size}
      className={cn(
        "absolute  h-8 w-8 rounded-full",
        orientation === "horizontal"
          ? "-left-12 top-1/2 -translate-y-1/2"
          : "-top-12 left-1/2 -translate-x-1/2 rotate-90",
        className
      )}
      disabled={!canScrollPrev}
      onClick={scrollPrev}
      {...props}
    >
      <ArrowLeft className="h-4 w-4" />
      <span className="sr-only">Previous slide</span>
    </Button>
  )
})
CarouselPrevious.displayName = "CarouselPrevious"

const CarouselNext = React.forwardRef<
  HTMLButtonElement,
  React.ComponentProps<typeof Button>
>(({ className, variant = "outline", size = "icon", ...props }, ref) => {
  const { orientation, scrollNext, canScrollNext } = useCarousel()

  return (
    <Button
      ref={ref}
      variant={variant}
      size={size}
      className={cn(
        "absolute h-8 w-8 rounded-full",
        orientation === "horizontal"
          ? "-right-12 top-1/2 -translate-y-1/2"
          : "-bottom-12 left-1/2 -translate-x-1/2 rotate-90",
        className
      )}
      disabled={!canScrollNext}
      onClick={scrollNext}
      {...props}
    >
      <ArrowRight className="h-4 w-4" />
      <span className="sr-only">Next slide</span>
    </Button>
  )
})
CarouselNext.displayName = "CarouselNext"

export {
  type CarouselApi,
  Carousel,
  CarouselContent,
  CarouselItem,
  CarouselPrevious,
  CarouselNext,
}

