import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";

export interface EditUserData {
  username: string;
  mobileNumber: string;
  password?: string;
}

interface EditUserDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
