export function EditUserDialog({ 
  open, 
  onOpenChange, 
  user,
  onSubmit,
  isPending 
}: EditUserDialogProps) {
  const [username, setUsername] = useState("");
  const [mobileNumber, setMobileNumber] = useState("");
