import bcrypt from 'bcrypt';
import { User, Ad, AdClick, Rating, Withdrawal } from './mongodb';
import mongoose from 'mongoose';

export interface IMongoStorage {
  // User operations
  getUser(id: string): Promise<any>;
  getUserByUsername(username: string): Promise<any>;
  getUserByEmail(email: string): Promise<any>;
  createUser(data: any): Promise<any>;
  updateUser(id: string, data: any): Promise<any>;
  getAllUsers(): Promise<any[]>;
  getPendingUsers(): Promise<any[]>;
  approveUser(id: string): Promise<any>;
  freezeUser(id: string): Promise<any>;

  // Financial operations
  addMilestoneAmount(userId: string, amount: string): Promise<any>;
  addMilestoneReward(userId: string, amount: string): Promise<any>;
  resetUserField(userId: string, field: string): Promise<any>;
  addUserFieldValue(userId: string, field: string, amount: string): Promise<any>;
  incrementAdsCompleted(userId: string): Promise<any>;
  incrementRestrictedAds(userId: string): Promise<any>;
  resetDestinationAmount(userId: string): Promise<any>;

  // Bank operations
  updateUserBankDetails(userId: string, bankDetails: any): Promise<any>;

  // Restriction operations
  setUserRestriction(userId: string, restriction: any): Promise<any>;
  removeUserRestriction(userId: string): Promise<any>;

  // Ad operations
  getAllAds(): Promise<any[]>;
  getAd(id: string): Promise<any>;
  createAd(data: any): Promise<any>;
  updateAd(id: string, data: any): Promise<any>;
  deleteAd(id: string): Promise<boolean>;
  getAdsWithUserClicks(userId: string): Promise<any[]>;

  // Ad click operations
  recordAdClick(userId: string, adId: string): Promise<any>;
  getUserAdClicks(userId: string): Promise<any[]>;

  // Rating operations
  getRatings(): Promise<any[]>;
  getUserRatings(userId: string): Promise<any[]>;
  createRating(userId: string, data: any): Promise<any>;
  deleteRating(id: string): Promise<boolean>;

  // Withdrawal operations
  getWithdrawals(): Promise<any[]>;
  getUserWithdrawals(userId: string): Promise<any[]>;
  createWithdrawal(userId: string, amount: string, bankDetails: any): Promise<any>;
  approveWithdrawal(id: string, adminId: string): Promise<any>;
  rejectWithdrawal(id: string, adminId: string, notes?: string): Promise<any>;
}

export class MongoStorage implements IMongoStorage {
  // User operations
  async getUser(id: string): Promise<any> {
    if (!mongoose.Types.ObjectId.isValid(id)) return undefined;
    return await User.findById(id);
  }

  async getUserByUsername(username: string): Promise<any> {
    return await User.findOne({ username });
  }

  async getUserByEmail(email: string): Promise<any> {
    return await User.findOne({ email });
  }

  async createUser(data: any): Promise<any> {
    const hashedPassword = await bcrypt.hash(data.password, 10);
    const user = new User({
      ...data,
      password: hashedPassword,
      status: 'pending',
      isAdmin: 0,
      destinationAmount: '25000.00',
      milestoneAmount: '0.00',
      milestoneReward: '0.00',
      totalAdsCompleted: 0,
      points: 100,
    });
    return await user.save();
  }

  async updateUser(id: string, data: any): Promise<any> {
    return await User.findByIdAndUpdate(id, data, { new: true });
  }

  async getAllUsers(): Promise<any[]> {
    return await User.find().sort({ registeredAt: -1 });
  }

  async getPendingUsers(): Promise<any[]> {
    return await User.find({ status: 'pending' }).sort({ registeredAt: -1 });
  }

  async approveUser(id: string): Promise<any> {
    return await User.findByIdAndUpdate(id, { status: 'active' }, { new: true });
  }

  async freezeUser(id: string): Promise<any> {
    return await User.findByIdAndUpdate(id, { status: 'frozen' }, { new: true });
  }

  // Financial operations
  async addMilestoneAmount(userId: string, amount: string): Promise<any> {
    const user = await User.findById(userId);
    if (!user) return undefined;
    const currentAmount = parseFloat(user.milestoneAmount || '0');
    const newAmount = (currentAmount + parseFloat(amount)).toFixed(2);
    return await User.findByIdAndUpdate(userId, { milestoneAmount: newAmount }, { new: true });
  }

  async addMilestoneReward(userId: string, amount: string): Promise<any> {
    const user = await User.findById(userId);
    if (!user) return undefined;
    const currentAmount = parseFloat(user.milestoneReward || '0');
    const newAmount = (currentAmount + parseFloat(amount)).toFixed(2);
    return await User.findByIdAndUpdate(userId, { milestoneReward: newAmount }, { new: true });
  }

  async resetUserField(userId: string, field: string): Promise<any> {
    const fieldMap: Record<string, any> = {
      'booking': { totalAdsCompleted: 0, restrictedAdsCompleted: 0 },
      'points': { milestoneAmount: '0.00' },
      'premiumTreasure': { milestoneReward: '0.00' },
      'normalTreasure': { destinationAmount: '0.00' },
      'bookingValue': { milestoneAmount: '0.00' },
      'ongoingMilestone': { ongoingMilestone: '0.00' },
    };

    const updateData = fieldMap[field];
    if (!updateData) {
      throw new Error(`Invalid field: ${field}`);
    }

    // If resetting booking, also delete all ad click history
    if (field === 'booking') {
      await AdClick.deleteMany({ userId });
    }

    return await User.findByIdAndUpdate(userId, updateData, { new: true });
  }

  async addUserFieldValue(userId: string, field: string, amount: string): Promise<any> {
    const user = await User.findById(userId);
    if (!user) return undefined;

    const numValue = parseFloat(amount);

    if (field === 'points') {
      if (numValue > 100) throw new Error('Points cannot exceed 100');
      return await User.findByIdAndUpdate(userId, { points: numValue }, { new: true });
    }

    const fieldMap: Record<string, string> = {
      'premiumTreasure': 'milestoneReward',
      'normalTreasure': 'destinationAmount',
      'bookingValue': 'milestoneAmount',
    };

    const dbField = fieldMap[field];
    if (!dbField) throw new Error(`Invalid field: ${field}`);

    const currentValue = parseFloat((user as any)[dbField] || '0');
    const newValue = (currentValue + numValue).toFixed(2);
    return await User.findByIdAndUpdate(userId, { [dbField]: newValue }, { new: true });
  }

  async incrementAdsCompleted(userId: string): Promise<any> {
    return await User.findByIdAndUpdate(userId, { $inc: { totalAdsCompleted: 1 } }, { new: true });
  }

  async incrementRestrictedAds(userId: string): Promise<any> {
    return await User.findByIdAndUpdate(userId, { $inc: { restrictedAdsCompleted: 1 } }, { new: true });
  }

  async resetDestinationAmount(userId: string): Promise<any> {
    return await User.findByIdAndUpdate(userId, { destinationAmount: '0.00' }, { new: true });
  }

  // Bank operations
  async updateUserBankDetails(userId: string, bankDetails: any): Promise<any> {
    return await User.findByIdAndUpdate(userId, {
      bankName: bankDetails.bankName,
      accountNumber: bankDetails.accountNumber,
      accountHolderName: bankDetails.accountHolderName,
      branchName: bankDetails.branchName,
    }, { new: true });
  }

  // Restriction operations
  async setUserRestriction(userId: string, restriction: any): Promise<any> {
    return await User.findByIdAndUpdate(userId, {
      restrictionAdsLimit: restriction.adsLimit,
      restrictionDeposit: restriction.deposit,
      restrictionCommission: restriction.commission,
      ongoingMilestone: restriction.ongoingMilestone || '0.00',
      restrictedAdsCompleted: 0,
    }, { new: true });
  }

  async removeUserRestriction(userId: string): Promise<any> {
    return await User.findByIdAndUpdate(userId, {
      $unset: { restrictionAdsLimit: '', restrictionDeposit: '', restrictionCommission: '' },
      ongoingMilestone: '0.00',
      restrictedAdsCompleted: 0,
    }, { new: true });
  }

  // Ad operations
  async getAllAds(): Promise<any[]> {
    return await Ad.find().sort({ createdAt: -1 });
  }

  async getAd(id: string): Promise<any> {
    if (!mongoose.Types.ObjectId.isValid(id)) return undefined;
    return await Ad.findById(id);
  }

  async createAd(data: any): Promise<any> {
    const ad = new Ad(data);
    return await ad.save();
  }

  async updateAd(id: string, data: any): Promise<any> {
    return await Ad.findByIdAndUpdate(id, data, { new: true });
  }

  async deleteAd(id: string): Promise<boolean> {
    const result = await Ad.findByIdAndDelete(id);
    return !!result;
  }

  async getAdsWithUserClicks(userId: string): Promise<any[]> {
    const ads = await Ad.find().sort({ createdAt: -1 });
    const clicks = await AdClick.find({ userId }).sort({ clickedAt: -1 });

    const clickMap = new Map();
    clicks.forEach(click => {
      const adIdStr = click.adId.toString();
      if (!clickMap.has(adIdStr) || click.clickedAt > clickMap.get(adIdStr)) {
        clickMap.set(adIdStr, click.clickedAt);
      }
    });

    return ads.map(ad => ({
      ...ad.toObject(),
      id: ad._id,
      lastClickedAt: clickMap.get(ad._id.toString()) || null,
    }));
  }

  // Ad click operations
  async recordAdClick(userId: string, adId: string): Promise<any> {
    const click = new AdClick({ userId, adId });
    return await click.save();
  }

  async getUserAdClicks(userId: string): Promise<any[]> {
    return await AdClick.find({ userId }).populate('adId').sort({ clickedAt: -1 });
  }

  // Rating operations
  async getRatings(): Promise<any[]> {
    return await Rating.find().populate('userId', 'username fullName').sort({ createdAt: -1 });
  }

  async getUserRatings(userId: string): Promise<any[]> {
    return await Rating.find({ userId }).sort({ createdAt: -1 });
  }

  async createRating(userId: string, data: any): Promise<any> {
    const rating = new Rating({
      userId,
      targetUsername: data.targetUsername,
      rating: data.rating,
      comment: data.comment,
    });
    return await rating.save();
  }

  async deleteRating(id: string): Promise<boolean> {
    const result = await Rating.findByIdAndDelete(id);
    return !!result;
  }

  // Withdrawal operations
  async getWithdrawals(): Promise<any[]> {
    return await Withdrawal.find().populate('userId', 'username fullName email').sort({ requestedAt: -1 });
  }

  async getUserWithdrawals(userId: string): Promise<any[]> {
    return await Withdrawal.find({ userId }).sort({ requestedAt: -1 });
  }

  async createWithdrawal(userId: string, amount: string, bankDetails: any): Promise<any> {
    const user = await User.findById(userId);
    if (!user) throw new Error('User not found');

    const milestoneAmount = parseFloat(user.milestoneAmount || '0');
    const withdrawalAmount = parseFloat(amount);

    if (withdrawalAmount > milestoneAmount) {
      throw new Error('Insufficient balance');
    }

    const withdrawal = new Withdrawal({
      userId,
      amount,
      bankFullName: bankDetails.fullName,
      bankAccountNumber: bankDetails.accountNumber,
      bankName: bankDetails.bankName,
      bankBranch: bankDetails.branch,
    });
    return await withdrawal.save();
  }

  async approveWithdrawal(id: string, adminId: string): Promise<any> {
    const withdrawal = await Withdrawal.findById(id);
    if (!withdrawal) throw new Error('Withdrawal not found');
    if (withdrawal.status !== 'pending') throw new Error('Withdrawal already processed');

    // Deduct from user's milestone amount
    const user = await User.findById(withdrawal.userId);
    if (user) {
      const currentAmount = parseFloat(user.milestoneAmount || '0');
      const newAmount = (currentAmount - parseFloat(withdrawal.amount)).toFixed(2);
      await User.findByIdAndUpdate(withdrawal.userId, { milestoneAmount: newAmount });
    }

    return await Withdrawal.findByIdAndUpdate(id, {
      status: 'approved',
      processedAt: new Date(),
      processedBy: adminId,
    }, { new: true });
  }

  async rejectWithdrawal(id: string, adminId: string, notes?: string): Promise<any> {
    return await Withdrawal.findByIdAndUpdate(id, {
      status: 'rejected',
      processedAt: new Date(),
      processedBy: adminId,
      notes,
    }, { new: true });
  }
}

export const mongoStorage = new MongoStorage();

