import React, { useState } from "react";
import { Link } from "wouter";
import bg from "../assets/bg-home.jpg";

export default function Register() {
  const [form, setForm] = useState({
    fullName: "",
    email: "",
    mobile: "",
    userCode: "",
    password: "",
    confirmPassword: "",
  });

  const [loading, setLoading] = useState(false);

  function update(field, value) {
    setForm({ ...form, [field]: value });
  }

  async function submitRegister(e) {
    e.preventDefault();

    if (form.password !== form.confirmPassword) {
      alert("Passwords do NOT match!");
      return;
    }

    setLoading(true);

    try {
      const res = await fetch("http://localhost:5000/api/auth/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          fullName: form.fullName,
          email: form.email,
          mobile: form.mobile,
          password: form.password,
          userCode: form.userCode,
        }),
      });

      const data = await res.json();
      setLoading(false);

      if (!res.ok) {
        alert(data.message || "Registration failed");
        return;
      }

      alert("Registration success! Your User Code: " + data.userCode);
      window.location.href = "/login"; // redirect

    } catch (err) {
      setLoading(false);
      alert("Server error. Try again.");
    }
  }

  return (
    <div
      className="register-bg"
      style={{ backgroundImage: `url(${bg})` }}
    >
      <div className="register-overlay">
        <div className="register-box">
          <h2 className="register-title">Create Account</h2>

          <form onSubmit={submitRegister} className="register-form">

            <label>Full Name</label>
            <input
              type="text"
              value={form.fullName}
              onChange={(e) => update("fullName", e.target.value)}
              placeholder="Enter your full name"
            />

            <label>Email</label>
            <input
              type="email"
              value={form.email}
              onChange={(e) => update("email", e.target.value)}
              placeholder="Enter your email"
            />

            <label>Mobile Number</label>
            <input
              type="text"
              value={form.mobile}
              onChange={(e) => update("mobile", e.target.value)}
              placeholder="Enter your mobile number"
            />

            <label>User Code (Optional)</label>
            <input
              value={form.userCode}
              onChange={(e) => update("userCode", e.target.value)}
              placeholder="Enter referral/user code"
            />

            <label>Password</label>
            <input
              type="password"
              value={form.password}
              onChange={(e) => update("password", e.target.value)}
              placeholder="Enter password"
            />

            <label>Confirm Password</label>
            <input
              type="password"
              value={form.confirmPassword}
              onChange={(e) => update("confirmPassword", e.target.value)}
              placeholder="Confirm password"
            />

            <button type="submit" className="register-btn">
              {loading ? "Please wait..." : "Register"}
            </button>
          </form>

          <Link href="/login">
            <a className="register-login-link">← Back to Login</a>
          </Link>
        </div>
      </div>
    </div>
  );
}

import { db } from "../db/connection.js";
import { users } from "../db/schema.js";
import bcrypt from "bcryptjs";
import { eq } from "drizzle-orm";

// REGISTER USER
export const registerUser = async (req, res) => {
  try {
    const { username, email, password, full_name } = req.body;

    if (!username || !email || !password || !full_name) {
      return res.status(400).json({ message: "All fields required" });
    }

    const existing = db.select().from(users).where(eq(users.email, email));
    if (existing.length > 0) {
      return res.status(400).json({ message: "Email already exists" });
    }

    const hashed = bcrypt.hashSync(password, 10);

    await db.insert(users).values({
      username,
      email,
      full_name,
      password: hashed,
      status: "pending"
    });

    res.json({ message: "Registration successful. Wait for admin approval." });

  } catch (err) {
    console.log(err);
    res.status(500).json({ message: "Server error" });
  }
};

// LOGIN USER
export const loginUser = async (req, res) => {
  try {
    const { email, password } = req.body;

    const user = db.select().from(users).where(eq(users.email, email))[0];

    if (!user) {
      return res.status(400).json({ message: "User not found" });
    }

    if (user.status !== "approved") {
      return res.status(403).json({ message: "Account not approved yet" });
    }

    const isValid = bcrypt.compareSync(password, user.password);
    if (!isValid) {
      return res.status(400).json({ message: "Incorrect password" });
    }

    req.session.user = {
      id: user.id,
      username: user.username,
      email: user.email,
      is_admin: user.is_admin
    };

    res.json({
      message: "Login success",
      user: req.session.user
    });

  } catch (err) {
    console.log(err);
    res.status(500).json({ message: "Server error" });
  }
};

export const adminMiddleware = (req, res, next) => {
  if (!req.user || req.user.isAdmin !== 1) {
    return res.status(403).json({ message: "Access denied - Admins only" });
  }

  next();
};

