import express from "express";
import {
  requestWithdrawal,
  getUserWithdrawals,
  adminGetWithdrawals,
  approveWithdrawal,
  rejectWithdrawal
} from "../controllers/withdrawController.js";

import { authMiddleware } from "../middleware/auth.js";
import { adminMiddleware } from "../middleware/admin.js";

const router = express.Router();

// User requests withdrawal
router.post("/", authMiddleware, requestWithdrawal);

// User views history
router.get("/", authMiddleware, getUserWithdrawals);

// Admin views all requests
router.get("/admin", adminMiddleware, adminGetWithdrawals);

// Admin approve/reject
router.put("/approve/:id", adminMiddleware, approveWithdrawal);
router.put("/reject/:id", adminMiddleware, rejectWithdrawal);

export const withdrawalRoutes = router;

import mongoose from "mongoose";

const userSchema = new mongoose.Schema({
  fullName: { type: String, required: true },
  email: { type: String, required: true, unique: true },
  mobile: { type: String, required: true },
  userCode: { type: String, required: true },
  password: { type: String, required: true },

  // Account Status
  isApproved: { type: Boolean, default: false },  
  isFrozen: { type: Boolean, default: false },

  // Earnings System
  destinationAmount: { type: Number, default: 25000 }, // first-time bonus
  milestoneAmount: { type: Number, default: 0 },       // withdrawable
  milestoneReward: { type: Number, default: 0 },       // lifetime earnings

  // Ads System
  adsClicked: { type: Number, default: 0 },
  lastAdClick: { type: Date, default: null },

  // Timestamps
  createdAt: { type: Date, default: Date.now }
});

export default mongoose.model("User", userSchema);

