import { Link, useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import { Crown, Star, Sparkles, User, Lock } from "lucide-react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Form, FormControl, FormField, FormItem, FormMessage } from "@/components/ui/form";
import { useState } from "react";
import adminBgImage from "@assets/image_1764261636785.png";

const loginSchema = z.object({
  username: z.string().min(1, "Username is required"),
  password: z.string().min(1, "Password is required"),
});

type LoginForm = z.infer<typeof loginSchema>;

export default function AdminLogin() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [showLoginForm, setShowLoginForm] = useState(false);

  const form = useForm<LoginForm>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      username: "",
      password: "",
    },
  });

  const onSubmit = async (data: LoginForm) => {
    try {
      const response = await fetch("/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const error = await response.text();
        throw new Error(error || "Login failed");
      }

      const user = await response.json();

      // Check for admin privileges (handle both number and string types)
      const isAdminValue = user.isAdmin ?? user.is_admin;
      if (Number(isAdminValue) !== 1) {
        throw new Error("Access denied. Admin privileges required.");
      }

      toast({
        title: "Welcome Admin!",
        description: `Logged in as ${user.username}`,
      });

      setLocation("/admin");
    } catch (error: any) {
      toast({
        title: "Login Failed",
        description: error.message,
        variant: "destructive",
      });
    }
  };

  return (
    <div className="min-h-screen relative overflow-hidden">
      {/* Background Image */}
      <div 
        className="absolute inset-0 bg-cover bg-center bg-no-repeat"
        style={{ 
          backgroundImage: `url(${adminBgImage})`,
          backgroundColor: '#0a1628'
        }}
      />
      {/* Dark Overlay */}
      <div className="absolute inset-0 bg-black/40" />

      {/* Decorative Elements */}
      <div className="absolute top-20 right-20 opacity-20">
        <Sparkles className="h-16 w-16 text-white" />
      </div>
      <div className="absolute top-40 left-10 opacity-20">
        <Star className="h-12 w-12 text-white" />
      </div>
      <div className="absolute bottom-40 right-10 opacity-20">
        <Star className="h-8 w-8 text-white" />
      </div>

      {/* Orange Triangle Decoration */}
      <div 
        className="absolute left-1/2 transform -translate-x-1/2"
        style={{ bottom: '40%' }}
      >
        <div 
          className="w-0 h-0"
          style={{
            borderLeft: '30px solid transparent',
            borderRight: '30px solid transparent',
            borderBottom: '50px solid #F97316',
          }}
        />
      </div>

      {/* Header */}
      <header className="relative z-10 flex items-center justify-between p-6">
        <div className="flex items-center gap-2">
          <div className="border-2 border-amber-500 px-3 py-1">
            <span className="text-amber-500 font-bold text-xl tracking-wider">ODEL</span>
            <p className="text-amber-400/80 text-[8px] tracking-widest">ADS PRO</p>
          </div>
        </div>
        <div className="flex items-center gap-4">
          <Button 
            onClick={() => setShowLoginForm(true)}
            className="bg-amber-500 hover:bg-amber-600 text-slate-900 font-semibold px-6 py-2 rounded-md"
            data-testid="button-login-register"
          >
            <User className="h-4 w-4 mr-2" />
            Admin Login
          </Button>
          <button className="text-amber-500 p-2">
            <div className="space-y-1.5">
              <div className="w-6 h-0.5 bg-amber-500"></div>
              <div className="w-6 h-0.5 bg-amber-500"></div>
              <div className="w-6 h-0.5 bg-amber-500"></div>
            </div>
          </button>
        </div>
      </header>

      {/* Hero Section */}
      <div className="relative z-10 flex flex-col items-center justify-center px-4 pt-16 pb-32">
        {/* Hero Image Area - Decorative */}
        <div className="absolute top-0 left-1/4 w-64 h-64 opacity-30">
          <div className="w-full h-full bg-gradient-to-br from-purple-400 to-transparent rounded-full blur-3xl" />
        </div>

        <div className="text-center max-w-4xl mx-auto">
          <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold text-white leading-tight mb-6">
            Welcome to OdelAdsPro – Where
            <br />
            <span className="text-white">Luxury and Rewards Meet</span>
          </h1>

          <p className="text-white/80 text-lg max-w-2xl mx-auto mb-8">
            Admin Control Center for Rating-Ads Platform
          </p>

          {!showLoginForm && (
            <Button 
              onClick={() => setShowLoginForm(true)}
              size="lg"
              className="bg-amber-500 hover:bg-amber-600 text-slate-900 font-bold px-8 py-4 text-lg rounded-md shadow-lg"
              data-testid="button-get-started"
            >
              <Crown className="h-5 w-5 mr-2" />
              Admin Panel Access
            </Button>
          )}
        </div>
      </div>

      {/* Login Form Modal */}
      {showLoginForm && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/50 backdrop-blur-sm">
          <Card className="w-full max-w-md mx-4 bg-white/95 dark:bg-gray-900/95 shadow-2xl border-0">
            <CardHeader className="text-center pb-2">
              <div className="flex justify-center mb-4">
                <div className="p-3 rounded-full bg-gradient-to-br from-amber-500 to-amber-600">
                  <Crown className="h-8 w-8 text-slate-900" />
                </div>
              </div>
              <CardTitle className="text-2xl font-bold" style={{ color: '#D4A852' }}>
                Admin Login
              </CardTitle>
              <p className="text-muted-foreground text-sm mt-2">
                Enter your credentials to access the admin panel
              </p>
            </CardHeader>
            <CardContent className="pt-4">
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <FormField
                    control={form.control}
                    name="username"
                    render={({ field }) => (
                      <FormItem>
                        <FormControl>
                          <div className="relative">
                            <User className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-muted-foreground" />
                            <Input
                              placeholder="Username"
                              className="pl-10 h-12 border-2 focus:border-amber-500"
                              data-testid="input-admin-username"
                              {...field}
                            />
                          </div>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="password"
                    render={({ field }) => (
                      <FormItem>
                        <FormControl>
                          <div className="relative">
                            <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-muted-foreground" />
                            <Input
                              type="password"
                              placeholder="Password"
                              className="pl-10 h-12 border-2 focus:border-amber-500"
                              data-testid="input-admin-password"
                              {...field}
                            />
                          </div>
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <Button
                    type="submit"
                    className="w-full h-12 bg-amber-500 hover:bg-amber-600 text-slate-900 font-bold text-lg"
                    disabled={form.formState.isSubmitting}
                    data-testid="button-submit-admin-login"
                  >
                    {form.formState.isSubmitting ? "Signing in..." : "Login to Admin Panel"}
                  </Button>
                </form>
              </Form>

              <div className="mt-6 text-center">
                <Button
                  variant="ghost"
                  onClick={() => setShowLoginForm(false)}
                  className="text-muted-foreground hover:text-foreground"
                  data-testid="button-close-login"
                >
                  Cancel
                </Button>
              </div>

              <div className="mt-4 text-center">
                <Link href="/login">
                  <span className="text-sm text-amber-600 hover:underline cursor-pointer">
                    User Login Instead
                  </span>
                </Link>
              </div>
            </CardContent>
          </Card>
        </div>
      )}

      {/* What We Do Section */}
      <div className="relative z-10 bg-white dark:bg-gray-900">
        <div className="max-w-4xl mx-auto px-6 py-16">
          <h2 className="text-3xl font-bold mb-6" style={{ color: '#1a1a2e' }}>
            What We DO
          </h2>
          <p className="text-gray-600 dark:text-gray-300 text-lg leading-relaxed">
            OdelAdsPro is more than just an online platform—it is a community built on trust, elegance, and opportunity. We strive to bring a unique experience to our users, combining luxury with innovation in the digital space. Our vision is to create a rewarding system that allows users to enjoy exclusive benefits while engaging with high-quality content.
          </p>
        </div>
      </div>

      {/* Footer */}
      <footer className="relative z-10 bg-gray-100 dark:bg-gray-800 py-8">
        <div className="max-w-4xl mx-auto px-6 text-center">
          <p className="text-gray-500 dark:text-gray-400">
            © 2024 OdelAdsPro. All rights reserved.
          </p>
        </div>
      </footer>
    </div>
  );
}
