client/src/pages/admin/user-detail.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { useRoute, useLocation } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { ArrowLeft, Check, Ban, DollarSign } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import type { User } from "@shared/schema";
import { useState } from "react";

export default function AdminUserDetail() {
  const [, params] = useRoute("/admin/users/:id");
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [depositAmount, setDepositAmount] = useState("");

  const userId = parseInt(params?.id || "0");

  const { data: user, isLoading } = useQuery<User>({
    queryKey: [`/api/admin/users/${userId}`],
    enabled: userId > 0,
  });

  const updateStatusMutation = useMutation({
    mutationFn: async (status: string) => {
      return apiRequest("POST", `/api/admin/users/${userId}/status`, { status });
    },
    onSuccess: () => {
      toast({
        title: "Status Updated",
        description: "User status has been updated successfully.",
      });
      queryClient.invalidateQueries({ queryKey: [`/api/admin/users/${userId}`] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Update Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const depositMutation = useMutation({
    mutationFn: async (amount: string) => {
      const response = await fetch(`/api/admin/users/${userId}/deposit`, {
        method: "POST",
        body: JSON.stringify({ amount }),
        headers: { "Content-Type": "application/json" },
      });
      if (!response.ok) {
        const error = await response.text();
        throw new Error(error);
      }
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Deposit Added",
        description: "The deposit has been added to the user's account successfully.",
      });
      queryClient.invalidateQueries({ queryKey: [`/api/admin/users/${userId}`] });
      setDepositAmount("");
    },
    onError: (error: Error) => {
      toast({
        title: "Deposit Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleDeposit = () => {
    const amount = parseFloat(depositAmount);
    if (isNaN(amount) || amount <= 0) {
      toast({
        variant: "destructive",
        title: "Invalid Amount",
        description: "Please enter a valid deposit amount",
      });
      return;
    }
    depositMutation.mutate(depositAmount);
  };

  const getStatusVariant = (status: string): "default" | "secondary" | "destructive" => {
    switch (status) {
      case "active":
        return "default";
      case "pending":
        return "secondary";
      case "frozen":
        return "destructive";
      default:
        return "secondary";
    }
  };

  if (isLoading) {
    return <div className="text-center py-8">Loading user details...</div>;
  }

  if (!user) {
    return (
      <div className="text-center py-8">
        <p className="text-muted-foreground mb-4">User not found</p>
        <Button onClick={() => setLocation("/admin/users")}>Go Back</Button>
      </div>
    );
  }

  return (
    <div>
      <Button
        variant="ghost"
        onClick={() => setLocation("/admin/users")}
        className="mb-6"
        data-testid="button-back"
      >
        <ArrowLeft className="h-4 w-4 mr-2" />
        Back to Users
      </Button>

      <div className="grid gap-6 md:grid-cols-2">
        <Card>
          <CardHeader>
            <CardTitle>User Information</CardTitle>
            <CardDescription>Complete user details</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <p className="text-sm font-medium text-muted-foreground">Full Name</p>
              <p className="text-lg" data-testid="text-fullname">
                {user.fullName}
              </p>
            </div>
            <div>
              <p className="text-sm font-medium text-muted-foreground">Username</p>
              <p className="text-lg" data-testid="text-username">
                @{user.username}
              </p>
            </div>
            <div>
              <p className="text-sm font-medium text-muted-foreground">Email</p>
              <p className="text-lg" data-testid="text-email">
                {user.email}
              </p>
            </div>
            <div>
              <p className="text-sm font-medium text-muted-foreground">Registered Date</p>
              <p className="text-lg">
                {new Date(user.registeredAt).toLocaleDateString()}{" "}
                {new Date(user.registeredAt).toLocaleTimeString()}
              </p>
            </div>
            <div>
              <p className="text-sm font-medium text-muted-foreground">Account Type</p>
              <p className="text-lg">
                {user.isAdmin === 1 ? "Administrator" : "Regular User"}
              </p>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Account Status</CardTitle>
            <CardDescription>Manage user account status</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <p className="text-sm font-medium text-muted-foreground mb-2">Current Status</p>
              <Badge
                variant={getStatusVariant(user.status)}
                className="text-base px-4 py-2"
                data-testid="badge-status"
              >
                {user.status.charAt(0).toUpperCase() + user.status.slice(1)}
              </Badge>
            </div>

            <div className="space-y-2 pt-4">
              <p className="text-sm font-medium text-muted-foreground">Quick Actions</p>
              <div className="flex flex-col gap-2">
                {user.status !== "active" && (
                  <Button
                    className="w-full"
                    onClick={() => updateStatusMutation.mutate("active")}
                    disabled={updateStatusMutation.isPending}
                    data-testid="button-approve"
                  >
                    <Check className="h-4 w-4 mr-2" />
                    Approve Account
                  </Button>
                )}
                {user.status !== "frozen" && (
                  <Button
                    variant="destructive"
                    className="w-full"
                    onClick={() => updateStatusMutation.mutate("frozen")}
                    disabled={updateStatusMutation.isPending}
                    data-testid="button-freeze"
                  >
                    <Ban className="h-4 w-4 mr-2" />
                    Freeze Account
                  </Button>
                )}
                {user.status === "frozen" && (
                  <Button
                    variant="outline"
                    className="w-full"
                    onClick={() => updateStatusMutation.mutate("active")}
                    disabled={updateStatusMutation.isPending}
                    data-testid="button-unfreeze"
                  >
                    <Check className="h-4 w-4 mr-2" />
                    Unfreeze Account
                  </Button>
                )}
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Financial Information */}
      <div className="grid gap-6 md:grid-cols-2 mt-6">
        <Card>
          <CardHeader>
            <CardTitle>Financial Information</CardTitle>
            <CardDescription>User's balance and transaction details</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <p className="text-sm font-medium text-muted-foreground">Destination Amount</p>
              <p className="text-2xl font-bold text-amber-500" data-testid="text-destination-amount">
                ₹ {parseFloat(user.destinationAmount).toLocaleString('en-IN', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
              </p>
              <p className="text-xs text-muted-foreground">Registration bonus</p>
            </div>
            <div>
              <p className="text-sm font-medium text-muted-foreground">Milestone Amount</p>
              <p className="text-2xl font-bold text-green-500" data-testid="text-milestone-amount">
                ₹ {parseFloat(user.milestoneAmount).toLocaleString('en-IN', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
              </p>
              <p className="text-xs text-muted-foreground">Withdrawable balance</p>
            </div>
            <div>
              <p className="text-sm font-medium text-muted-foreground">Milestone Reward</p>
              <p className="text-2xl font-bold text-blue-500" data-testid="text-milestone-reward">
                ₹ {parseFloat(user.milestoneReward).toLocaleString('en-IN', { minimumFractionDigits: 2, maximumFractionDigits: 2 })}
              </p>
              <p className="text-xs text-muted-foreground">Total withdrawn</p>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle>Add Deposit</CardTitle>
            <CardDescription>Add funds to user's Milestone Amount</CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="deposit-amount">Deposit Amount (LKR)</Label>
              <Input
                id="deposit-amount"
                type="number"
                placeholder="Enter amount"
                value={depositAmount}
                onChange={(e) => setDepositAmount(e.target.value)}
                data-testid="input-deposit-amount"
              />
            </div>
            <Button
              onClick={handleDeposit}
              disabled={depositMutation.isPending || !depositAmount}
              className="w-full"
              data-testid="button-add-deposit"
            >
              <DollarSign className="h-4 w-4 mr-2" />
              {depositMutation.isPending ? "Adding..." : "Add Deposit"}
            </Button>
            <p className="text-sm text-muted-foreground">
              This will be added to the user's withdrawable balance (Milestone Amount).
            </p>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/admin/premium.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { useState } from "react";
import type { User } from "@shared/schema";
import { Search, Mail, Phone } from "lucide-react";
import { RestrictUserDialog } from "@/components/restrict-user-dialog";
import { ResetConfirmDialog } from "@/components/reset-confirm-dialog";
import { AddValueDialog } from "@/components/add-value-dialog";
import { EditUserDialog, type EditUserData } from "@/components/edit-user-dialog";
import { EditBankDialog, type EditBankData } from "@/components/edit-bank-dialog";

export default function AdminPremium() {
  const { toast } = useToast();
  const [searchQuery, setSearchQuery] = useState("");
  const [restrictDialogOpen, setRestrictDialogOpen] = useState(false);
  const [dialogMode, setDialogMode] = useState<"create" | "edit">("create");
  const [resetDialogOpen, setResetDialogOpen] = useState(false);
  const [addValueDialogOpen, setAddValueDialogOpen] = useState(false);
  const [editUserDialogOpen, setEditUserDialogOpen] = useState(false);
  const [editBankDialogOpen, setEditBankDialogOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [resetField, setResetField] = useState<string>("");
  const [addValueField, setAddValueField] = useState<string>("");

  const { data: users = [], isLoading } = useQuery<User[]>({
    queryKey: ["/api/admin/users"],
    refetchOnMount: "always",
    refetchOnWindowFocus: true,
  });

  // Filter users based on search query
  const filteredUsers = users.filter(user => {
    const query = searchQuery.toLowerCase();
    return (
      user.fullName.toLowerCase().includes(query) ||
      user.username.toLowerCase().includes(query) ||
      user.email.toLowerCase().includes(query) ||
      user.id.toString().includes(query)
    );
  });

  const setRestrictionMutation = useMutation({
    mutationFn: async ({ userId, adsLimit, deposit, commission, pendingAmount }: { 
      userId: number; 
      adsLimit: number; 
      deposit: string; 
      commission: string;
      pendingAmount: string;
    }) => {
      const response = await fetch(`/api/admin/users/${userId}/restrict`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ adsLimit, deposit, commission, pendingAmount }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || "Failed to set restriction");
      }

      return data;
    },
    onSuccess: () => {
      // Force refetch instead of just invalidating
      queryClient.refetchQueries({ queryKey: ["/api/admin/users"] });
      setRestrictDialogOpen(false);
      setSelectedUser(null);
      toast({
        title: "Restriction Applied",
        description: "User restriction has been set successfully. Milestone Amount has been reduced by the deposit amount.",
      });
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Restriction Failed",
        description: error.message || "Failed to apply restriction. Please check the input values.",
      });
    },
  });

  const resetMutation = useMutation({
    mutationFn: async ({ userId, field }: { userId: number; field: string }) => {
      const response = await fetch(`/api/admin/users/${userId}/reset`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ field }),
      });
      if (!response.ok) throw new Error("Failed to reset");
      return response.json();
    },
    onSuccess: () => {
      // Force refetch instead of just invalidating
      queryClient.refetchQueries({ queryKey: ["/api/admin/users"] });
      toast({
        title: "Reset Successful",
        description: "Field has been reset to 0",
      });
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Reset Failed",
        description: error.message,
      });
    },
  });

  const addValueMutation = useMutation({
    mutationFn: async ({ userId, field, amount }: { userId: number; field: string; amount: string }) => {
      const response = await fetch(`/api/admin/users/${userId}/add-value`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ field, amount }),
      });
      if (!response.ok) throw new Error("Failed to add value");
      return response.json();
    },
    onSuccess: () => {
      // Force refetch instead of just invalidating
      queryClient.refetchQueries({ queryKey: ["/api/admin/users"] });
      toast({
        title: "Value Added",
        description: "Amount has been added successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Add Failed",
        description: error.message,
      });
    },
  });

  const editUserMutation = useMutation({
    mutationFn: async ({ userId, data }: { userId: number; data: EditUserData }) => {
      const response = await fetch(`/api/admin/users/${userId}/details`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const error = await response.text();
        throw new Error(error || "Failed to update user details");
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.refetchQueries({ queryKey: ["/api/admin/users"] });
      setEditUserDialogOpen(false);
      setSelectedUser(null);
      toast({
        title: "User Updated",
        description: "User details have been updated successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Update Failed",
        description: error.message || "Failed to update user details.",
      });
    },
  });

  const editBankMutation = useMutation({
    mutationFn: async ({ userId, data }: { userId: number; data: EditBankData }) => {
      const response = await fetch(`/api/admin/users/${userId}/bank`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const error = await response.text();
        throw new Error(error || "Failed to update bank details");
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.refetchQueries({ queryKey: ["/api/admin/users"] });
      setEditBankDialogOpen(false);
      setSelectedUser(null);
      toast({
        title: "Bank Details Updated",
        description: "Bank details have been updated successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Update Failed",
        description: error.message || "Failed to update bank details.",
      });
    },
  });

  const handleRestrict = (userId: number) => {
    const user = users.find(u => u.id === userId);
    if (!user) return;

    setSelectedUser(user);

    // Check if user already has a restriction - if yes, open in EDIT mode
    if ((user as any).restrictionAdsLimit !== null && (user as any).restrictionAdsLimit !== undefined) {
      setDialogMode("edit");
    } else {
      setDialogMode("create");
    }

    setRestrictDialogOpen(true);
  };

  const handleRestrictionSubmit = (data: { adsLimit: number; deposit: string; commission: string; pendingAmount: string }) => {
    if (!selectedUser) return;
    setRestrictionMutation.mutate({
      userId: selectedUser.id,
      ...data,
    });
  };

  const handleReset = (userId: number, field: string) => {
    const user = users.find(u => u.id === userId);
    if (!user) return;

    setSelectedUser(user);
    setResetField(field);
    setResetDialogOpen(true);
  };

  const handleResetConfirm = () => {
    if (!selectedUser || !resetField) return;
    resetMutation.mutate({ userId: selectedUser.id, field: resetField });
    setResetDialogOpen(false);
    setSelectedUser(null);
    setResetField("");
  };

  const handleAdd = (userId: number, field: string) => {
    const user = users.find(u => u.id === userId);
    if (!user) return;

    setSelectedUser(user);
    setAddValueField(field);
    setAddValueDialogOpen(true);
  };

  const handleAddValueSubmit = (amount: string) => {
    if (!selectedUser || !addValueField) return;

    addValueMutation.mutate({ 
      userId: selectedUser.id, 
      field: addValueField, 
      amount 
    });

    setAddValueDialogOpen(false);
    setSelectedUser(null);
    setAddValueField("");
  };

  const handleEditUser = (userId: number) => {
    const user = users.find(u => u.id === userId);
    if (!user) return;

    setSelectedUser(user);
    setEditUserDialogOpen(true);
  };

  const handleEditUserSubmit = (data: EditUserData) => {
    if (!selectedUser) return;
    editUserMutation.mutate({ userId: selectedUser.id, data });
  };

  const handleEditBank = (userId: number) => {
    const user = users.find(u => u.id === userId);
    if (!user) return;

    setSelectedUser(user);
    setEditBankDialogOpen(true);
  };

  const handleEditBankSubmit = (data: EditBankData) => {
    if (!selectedUser) return;
    editBankMutation.mutate({ userId: selectedUser.id, data });
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-96">
        <p className="text-muted-foreground">Loading premium users...</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between bg-card border rounded-lg p-4">
        <h1 className="text-xl font-bold" style={{ color: '#B8936B' }}>
          Premium
        </h1>
        <div className="flex items-center gap-4 flex-1 max-w-2xl mx-8">
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <Input
              placeholder="Search"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10"
              data-testid="input-search-premium"
            />
          </div>
        </div>
        <p className="text-sm text-muted-foreground">
          Hello, did you check the List of Premium Packages? 📦
        </p>
