client/src/pages/admin/withdrawals.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";
import type { Withdrawal, User } from "@shared/schema";
import { format } from "date-fns";
import { useState, useMemo } from "react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";

export default function AdminWithdrawals() {
  const { toast } = useToast();
  const [rejectDialogOpen, setRejectDialogOpen] = useState(false);
  const [selectedWithdrawal, setSelectedWithdrawal] = useState<Withdrawal | null>(null);
  const [rejectNotes, setRejectNotes] = useState("");
  const [searchQuery, setSearchQuery] = useState("");

  const { data: withdrawals = [], isLoading: withdrawalsLoading } = useQuery<Withdrawal[]>({
    queryKey: ["/api/admin/withdrawals"],
  });

  const { data: users = [], isLoading: usersLoading } = useQuery<User[]>({
    queryKey: ["/api/admin/users"],
  });

  const approveMutation = useMutation({
    mutationFn: async (id: number) => {
      const response = await fetch(`/api/admin/withdrawals/${id}/approve`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
      });
      if (!response.ok) {
        const error = await response.text();
        throw new Error(error);
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/withdrawals"] });
      queryClient.invalidateQueries({ queryKey: ["/api/admin/users"] });
      toast({
        title: "Withdrawal Approved",
        description: "The withdrawal has been processed successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Approval Failed",
        description: error.message,
      });
    },
  });

  const rejectMutation = useMutation({
    mutationFn: async ({ id, notes }: { id: number; notes: string }) => {
      const response = await fetch(`/api/admin/withdrawals/${id}/reject`, {
        method: "POST",
        body: JSON.stringify({ notes }),
        headers: { "Content-Type": "application/json" },
      });
      if (!response.ok) {
        const error = await response.text();
        throw new Error(error);
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/withdrawals"] });
      toast({
        title: "Withdrawal Rejected",
        description: "The withdrawal request has been rejected",
      });
      setRejectDialogOpen(false);
      setSelectedWithdrawal(null);
      setRejectNotes("");
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Rejection Failed",
        description: error.message,
      });
    },
  });

  const handleApprove = (withdrawal: Withdrawal) => {
    approveMutation.mutate(withdrawal.id);
  };

  const handleRejectClick = (withdrawal: Withdrawal) => {
    setSelectedWithdrawal(withdrawal);
    setRejectDialogOpen(true);
  };

  const handleRejectConfirm = () => {
    if (selectedWithdrawal) {
      rejectMutation.mutate({ id: selectedWithdrawal.id, notes: rejectNotes });
    }
  };

  const userMap = useMemo(() => {
    const map = new Map<number, User>();
    users.forEach(user => map.set(user.id, user));
    return map;
  }, [users]);

  const filteredWithdrawals = useMemo(() => {
    if (!searchQuery.trim()) return withdrawals;

    const query = searchQuery.toLowerCase();
    return withdrawals.filter((withdrawal) => {
      const user = userMap.get(withdrawal.userId);
      return (
        withdrawal.id.toString().includes(query) ||
        user?.fullName?.toLowerCase().includes(query) ||
        user?.username?.toLowerCase().includes(query) ||
        withdrawal.amount.includes(query)
      );
    });
  }, [withdrawals, userMap, searchQuery]);

  const isLoading = withdrawalsLoading || usersLoading;

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-96">
        <p className="text-muted-foreground">Loading withdrawals...</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header Section */}
      <div className="flex items-center justify-between gap-4 bg-card border rounded-lg p-4">
        <h1 className="text-xl font-bold text-amber-600" style={{ color: '#B8936B' }}>
          Withdraw Request List
        </h1>
        <Input
          placeholder="Search"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="max-w-xs"
          data-testid="input-search"
        />
        <p className="text-sm text-muted-foreground whitespace-nowrap">
          Hello, have you checked your withdrawal status?😊?
        </p>
      </div>

      {/* Table */}
      <div className="bg-card border rounded-lg overflow-hidden">
        <Table>
          <TableHeader>
            <TableRow className="bg-muted/50">
              <TableHead className="font-bold uppercase text-amber-700" style={{ color: '#B8936B' }}>ID</TableHead>
              <TableHead className="font-bold uppercase text-amber-700" style={{ color: '#B8936B' }}>NAME</TableHead>
              <TableHead className="font-bold uppercase text-amber-700" style={{ color: '#B8936B' }}>DETAILS</TableHead>
              <TableHead className="font-bold uppercase text-amber-700" style={{ color: '#B8936B' }}>PRICE</TableHead>
              <TableHead className="font-bold uppercase text-amber-700" style={{ color: '#B8936B' }}>PHONE</TableHead>
              <TableHead className="font-bold uppercase text-amber-700" style={{ color: '#B8936B' }}>ACCEPT</TableHead>
              <TableHead className="font-bold uppercase text-amber-700" style={{ color: '#B8936B' }}>REJECT</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {filteredWithdrawals.length === 0 ? (
              <TableRow>
                <TableCell colSpan={7} className="text-center py-8 text-muted-foreground">
                  No withdrawal requests found
                </TableCell>
              </TableRow>
            ) : (
              filteredWithdrawals.map((withdrawal) => {
                const user = userMap.get(withdrawal.userId);
                return (
                  <TableRow key={withdrawal.id} data-testid={`withdrawal-row-${withdrawal.id}`}>
                    <TableCell className="font-medium" data-testid={`withdrawal-id-${withdrawal.id}`}>
                      {withdrawal.id}
                    </TableCell>
                    <TableCell data-testid={`withdrawal-name-${withdrawal.id}`}>
                      {user?.fullName || 'Unknown User'}
                    </TableCell>
                    <TableCell data-testid={`withdrawal-details-${withdrawal.id}`}>
                      <div className="space-y-1">
                        <p className="text-sm">
                          <span className="text-muted-foreground">Requested:</span>{" "}
                          {format(new Date(withdrawal.requestedAt), "PPP")}
                        </p>
                        {withdrawal.processedAt && (
                          <p className="text-sm">
                            <span className="text-muted-foreground">Processed:</span>{" "}
                            {format(new Date(withdrawal.processedAt), "PPP")}
                          </p>
                        )}
                        <p className="text-sm">
                          <span className="text-muted-foreground">Status:</span>{" "}
                          <span className={
                            withdrawal.status === 'approved' ? 'text-green-500' :
                            withdrawal.status === 'rejected' ? 'text-red-500' :
                            'text-yellow-500'
                          }>
                            {withdrawal.status.toUpperCase()}
                          </span>
                        </p>
                        {withdrawal.notes && (
                          <p className="text-sm italic text-muted-foreground">
                            Note: {withdrawal.notes}
                          </p>
                        )}
                      </div>
                    </TableCell>
                    <TableCell className="font-bold" data-testid={`withdrawal-price-${withdrawal.id}`}>
                      ₹ {parseFloat(withdrawal.amount).toLocaleString('en-IN', { 
                        minimumFractionDigits: 2, 
                        maximumFractionDigits: 2 
                      })}
                    </TableCell>
                    <TableCell data-testid={`withdrawal-phone-${withdrawal.id}`}>
                      {user?.email || 'N/A'}
                    </TableCell>
                    <TableCell>
                      {withdrawal.status === 'pending' ? (
                        <Button
                          size="sm"
                          onClick={() => handleApprove(withdrawal)}
                          disabled={approveMutation.isPending}
                          className="bg-green-600 hover:bg-green-700"
                          data-testid={`button-accept-${withdrawal.id}`}
                        >
                          ACCEPT
                        </Button>
                      ) : withdrawal.status === 'approved' ? (
                        <span className="text-green-500 font-medium">✓ Approved</span>
                      ) : (
                        <span className="text-muted-foreground">-</span>
                      )}
                    </TableCell>
                    <TableCell>
                      {withdrawal.status === 'pending' ? (
                        <Button
                          size="sm"
                          onClick={() => handleRejectClick(withdrawal)}
                          disabled={rejectMutation.isPending}
                          variant="destructive"
                          data-testid={`button-reject-${withdrawal.id}`}
                        >
                          REJECT
                        </Button>
                      ) : withdrawal.status === 'rejected' ? (
                        <span className="text-red-500 font-medium">✗ Rejected</span>
                      ) : (
                        <span className="text-muted-foreground">-</span>
                      )}
                    </TableCell>
                  </TableRow>
                );
              })
            )}
          </TableBody>
        </Table>
      </div>

      {/* Reject Dialog */}
      <Dialog open={rejectDialogOpen} onOpenChange={setRejectDialogOpen}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>Reject Withdrawal Request</DialogTitle>
            <DialogDescription>
              Are you sure you want to reject this withdrawal request?
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="notes">Rejection Reason (Optional)</Label>
              <Textarea
                id="notes"
                placeholder="Enter reason for rejection..."
                value={rejectNotes}
                onChange={(e) => setRejectNotes(e.target.value)}
                data-testid="input-reject-notes"
              />
            </div>
          </div>
          <DialogFooter>
            <Button variant="ghost" onClick={() => setRejectDialogOpen(false)}>
              Cancel
            </Button>
            <Button
              variant="destructive"
              onClick={handleRejectConfirm}
              disabled={rejectMutation.isPending}
              data-testid="button-confirm-reject"
            >
              {rejectMutation.isPending ? "Rejecting..." : "Confirm Rejection"}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

